import axios from "axios";

const Post = ({ id, comments }) => <h1>post page ðŸ˜Ž{id}</h1>;

Post.getInitialProps = async ({ query }) => {
  const url: string = "https://jsonplaceholder.typicode.com/comments?postId=3";
  const res = await axios.get(url);
  return { ...query, comments: res.data };
};

export default Post;
